	private void jButton1ActionPerformed(java.awt.event.ActionEvent evt)
			throws Exception {

		Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");

		Connection con = DriverManager.getConnection("jdbc:odbc:flight", "123",
				"123");

		Statement stmt = con.createStatement();
		String n = jTextField1.getText();

		String m = jPasswordField1.getText();

		if (n.equals("") || m.equals("")) {

			jOptionPane1.showMessageDialog(Index.this, "用户名和密码不能为空！");

			return;
		}
		//final JComboBox jcb = new JComboBox(new String[] { "旅客", "管理员" });
		String a = ((String) jComboBox1.getSelectedItem()).equals("旅客") ? "0"
				: "1";

		ResultSet rs = stmt
				.executeQuery("select * from user where PassagerName='" + n
						+ "' and passward='" + m + "' and power=" + a + " ");

		if (rs.next()) {
			if (rs.getString("power").equals("0")) {

				jOptionPane4.showMessageDialog(Index.this, "登陆成功！欢迎旅客"
						+ rs.getString("PassagerName") + " ！");
				String k = rs.getString("id");
				stmt
						.executeUpdate("INSERT INTO take_info(PassagerName,id) VALUES('"
								+ n + "','" + k + "')");

				new User().setVisible(true);
				dispose();

			} else {

				jOptionPane4.showMessageDialog(Index.this, "登陆成功！欢迎管理员"
						+ rs.getString("PassagerName") + " ！");
				new BackGround().setVisible(true);
				dispose();
			}

		}

		else {
			jOptionPane4.showMessageDialog(Index.this, "用户名、密码与身份不匹配！！");

		}
	}

