	private void jButton1ActionPerformed(java.awt.event.ActionEvent evt)
			throws Exception {
		Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");
		Connection con = DriverManager.getConnection("jdbc:odbc:flight", "123",
				"123");
		Statement stmt = con.createStatement();
		String a = jTextField1.getText();
		if (a.equals("")) {
			jOptionPane1.showMessageDialog(null, "튃턺풝미", "엀퓝",
					jOptionPane1.ERROR_MESSAGE);
			return;
		}
		ResultSet rs = stmt
				.executeQuery("select * from take_info where OrderForm=" + a
						+ "");
		if (rs.next()) {
			if (a.equals(rs.getString("OrderForm"))) {
				
				String b=rs.getString("FlightType");
				String c=rs.getString("SeatNumber");
				String d=rs.getString("FlightNumber");
				if(b.equals("쭰쨲쒯")){
				jOptionPane2.showMessageDialog(null, "퐒월톷붞");
				stmt.executeUpdate("delete from take_info where OrderForm=" + a
						+ "");
				
				stmt.executeUpdate("UPDATE flight_info SET CheapNumber=CheapNumber+"+c+" WHERE FlightNumber='"+d+"'");
			}else{
				jOptionPane2.showMessageDialog(null, "퐒월톷붞");
				stmt.executeUpdate("delete from take_info where OrderForm=" + a
						+ "");
				
				stmt.executeUpdate("UPDATE flight_info SET BusyNumber=BusyNumber+"+c+" WHERE FlightNumber='"+d+"'");
			}
			}} else {
			jOptionPane2.showMessageDialog(null, "퐒월쒙톷붞");
		}

		stmt.close();
		con.close();
	}

